<?php

namespace Database\Seeders;

use App\Models\Type_of_interest;
use Illuminate\Database\Seeder;
use Illuminate\Database\Console\Seeds\WithoutModelEvents;

class TypeOfInterestSeeder extends Seeder
{
    /**
     * Run the database seeds.
     */
    public function run(): void
    {
        
        Type_of_interest::create([
            'name' => 'Wisata', 
            'image' => $this->uploadToStorage('1', 'data:image/png;base64,'),
            'description' => ''
        ]);
        Type_of_interest::create([
            'name' => 'Paket Wisata', 
            'image' => $this->uploadToStorage('2', 'data:image/png;base64,'),
            'description' => ''
        ]);
        Type_of_interest::create([
            'name' => 'Event', 
            'image' => $this->uploadToStorage('3', 'data:image/png;base64,'),
            'description' => ''
        ]);
        Type_of_interest::create([
            'name' => 'Kuliner', 
            'image' => $this->uploadToStorage('4', 'data:image/png;base64,'),
            'description' => ''
        ]);
        Type_of_interest::create([
            'name' => 'Akomodasi', 
            'image' => $this->uploadToStorage('5', 'data:image/png;base64,'),
            'description' => ''
        ]);
        Type_of_interest::create([
            'name' => 'Desa Wisata', 
            'image' => $this->uploadToStorage('6', 'data:image/png;base64,'),
            'description' => ''
        ]);
    }

    public function uploadToStorage($no = "1", $image = "")
    {
        if ($image != "") {
            $base64_string = $image;
            $output_file = "/public/storage";
            $splited = explode(',', substr( $base64_string , 5 ) , 2);
            $mime = $splited[0];
            $mime_split_without_base64=explode(';', $mime,2);
            $mime_split=explode('/', $mime_split_without_base64[0],2);
            $file_type = $mime_split[1];
            $is_file = "/".$no."_toi_".date("YmdHis").".".$file_type;

            file_put_contents($this->public_path('storage') . $is_file, file_get_contents($base64_string));

            return $output_file . $is_file;
        }
        return null;
    }

    function public_path($path=null)
    {
            return rtrim(app()->basePath('public/'.$path), '/');
    }

}
